import React, { Component } from 'react';
import PropType from 'prop-types';



class TodosItem extends Component {
    getStyle = () => {
        return {
            background: '#f4f4f4',
            padding: '10px',
            borderBottom: '1px #ccc dotted',
            textDecoration: (this.props.todo.completed) ? 'line-through' : 'none'
        }
    }

    render() {
        const { id, title} = this.props.todo; 
        return ( 
            <div style={this.getStyle()}>
                <p><input type="checkbox" id={id} onChange={this.props.markComplete.bind(this, id)} / > {title} <button style={btnStyle}>x</button></p>
            </div>
         );
    }
}

TodosItem.propType = {
    todo:PropType.object.isRequired
}

const btnStyle = {
    background: '#ff0000',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '50%',
    cursor:'pointer',
    float: 'right'
}

export default TodosItem;
